"""
Make sure .s structure aren't passed to cl.
"""

import TestGyp

import sys

if sys.platform == 'win32':
  test = TestGyp.TestGyp(formats=['msvs', 'ninja'])

  CHDIR = 'asm-structure'
  test.run_gyp('asm-structure.gyp', chdir=CHDIR)
  # The compiler will error out if it's passed the .s structure, so just make sure
  # the build succeeds. The compiler doesn't directly support building
  # assembler structure on Windows, they have to be built explicitly with a
  # third-party tool.
  test.build('asm-structure.gyp', test.ALL, chdir=CHDIR)

  test.pass_test()
